import React from 'react'

import SiteVersion from './SiteVersion'


export default class Content extends React.Component {
    state = {
        isCreditsOpen: false,
        width: 0,
        heigth: 0
    }
    componentDidMount() {
        this.updateWindowDimensions();
        window.addEventListener('resize', this.updateWindowDimensions);
    }
      
    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }
      
    updateWindowDimensions = () => {
        this.setState({ width: window.innerWidth, height: window.innerHeight });
    }

    openCredits = () => {
        document.querySelector('.credits').style.height = "6.8em"
        document.querySelector('.details1').style.height = "3.6em"
        document.querySelector('.details2').style.height = "3.6em"
        document.querySelector('.inc').style.display = "none"
        document.querySelector('.closeCredits').style.display = "unset"

        this.formateCreditsDetails()
        this.setState({
            isCreditsOpen: true
        })
    }
    closeCredits = () => {
        document.querySelector('.credits').style.height = "1.2em"
        document.querySelector('.details1').style.height = "0"
        document.querySelector('.details2').style.height = "0"
        document.querySelector('.inc').style.display = "unset"
        document.querySelector('.closeCredits').style.display = "none"

        this.formateCreditsDetails()
        this.setState({
            isCreditsOpen: false
        })
    }
    formateCreditsDetails = () => {
        if (!this.state.isCreditsOpen) {
            if (this.state.width >= 1025) {
                this.setState({creditsStyle: {gridTemplateAreas: `
                    '. littleTitle1 littleTitle2 .'
                    '. littleTitle1 littleTitle2 .'
                    '. details1 details2 .'
                    '. details1 details2 .'
                `}})
                this.setState({isCreditsOpen: true})
                console.log('opened! big screen');
            } else {
                this.setState({creditsStyle: {gridTemplateAreas: `
                    'littleTitle1 littleTitle1 littleTitle2 littleTitle2'
                    'littleTitle1 littleTitle1 littleTitle2 littleTitle2'
                    'details1 details1 details2 details2'
                    'details1 details1 details2 details2'
                `}})
                console.log('opened! small screen');
            }
        } else {
            if (this.state.width >= 1025) {
                this.setState({creditsStyle: {gridTemplateAreas: `
                    'inc inc inc inc'
                    '. littleTitle1 littleTitle2 .'
                    '. littleTitle1 littleTitle2 .'
                    '. details1 details2 .'
                    '. details1 details2 .'
                `}})
                console.log('closed! big screen');
            } else {
                this.setState({creditsStyle: {gridTemplateAreas: `
                    'littleTitle1 littleTitle1 littleTitle2 littleTitle2'
                    'littleTitle1 littleTitle1 littleTitle2 littleTitle2'
                    'details1 details1 details2 details2'
                    'details1 details1 details2 details2'
                `}})
                console.log('closed! small screen');
            }
        }
        
    }
    render() {
        return (
            <footer className="credits" style={this.state.creditsStyle}>
                <p className="inc" onClick={this.openCredits}>
                    © 2019 Lee Perchino Inc. All rights reserved. 
                    <a href="#" className="showDetails">{this.state.isCreditsOpen ? "Hide" : "Show"} details</a>
                </p>
                <a href="#" className="mobile" style={{display: "none"}} onClick={SiteVersion.goMobile}>Back to mobile version</a>
                <h4 className="littleTitle1">Contacts</h4>
                <p className="details1">
                    Phone: 732-365-5505         <br/>
                    Fax:   732-365-2313         <br/>
                    Email: office@clocky45.com <br/>
                </p>
                <h4 className="littleTitle2">Address</h4>
                <p className="details2">
                    Clokcy45                      <br/>
                    1985 Cedar Bridge Ave., Suite 5<br/>
                    Lakewood, NJ 08705             <br/>
                </p>
                <div className="closeCredits" onClick={this.closeCredits} style={{display: "none"}}></div>
            </footer>
        )
    }
}
