import React from 'react'

import SiteVersion from './SiteVersion'

const closeNav = () => {
    document.getElementById("mySidenav").style.width = "0"
    document.querySelector(".closeSidenav").style.display = "none"
}

export default class Content extends React.Component {

    
    render() {
        return (
            <div className="sidebar">
                <div id="mySidenav" className="sidenav">
                    <a href="javascript:void(0)" className="closebtn" onClick={this.props.closeNav}>&times;</a>
                    <a href="#">About</a>
                    <a href="#">Services</a>
                    <a href="#">Clients</a>
                    <a href="#">Contact</a>
                    <a href="#" className="desktop" onClick={SiteVersion.goDesktop}>Desktop version</a>
                </div>
                <div className="closeSidenav" onClick={closeNav}></div>
            </div>
        )
    }
}
