import React from 'react';
// import Home from './routes/home/Home';
import { Route, Switch, Link } from 'react-router-dom'

import Sidebar from './routes/home/components/Sidebar'
import Header from './routes/home/components/Header'
import Content from './routes/home/components/Content'
import Footer from './routes/home/components/Footer'

const Home = () => (
    <div className="page-container">
        <Sidebar closeNav={closeNav}/>
        <Header openNav={openNav}/>
        <Content />
        <Footer />
    </div>
)
const closeNav = () => {
    document.getElementById("mySidenav").style.width = "0"
    document.querySelector(".closeSidenav").style.display = "none"
}
const openNav = () => {
    document.getElementById("mySidenav").style.width = "700px"
    document.querySelector(".closeSidenav").style.display = "unset"
}


export default class App extends React.Component {

    render() {
        return (
            <Route path="/" component={Home}/>
        )
    }
}
