import React from 'react'

import SiteVersion from './SiteVersion'


export default class Content extends React.Component {
    state = {
        isCreditsOpen: false
    }
    toggleCredits = () => {
        if (!this.state.isCreditsOpen) {
            document.querySelector('.credits').style.height = "3.6em"
            document.querySelector('.details').style.height = "1.2em"

        } else {
            document.querySelector('.credits').style.height = "1.2em"
            document.querySelector('.details').style.height = "0"
        }
        this.setState(prevState => ({
            isCreditsOpen: !prevState.isCreditsOpen
        }))
    }   
    render() {
        return (
            <footer onClick={this.toggleCredits} className="credits">
